import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

export const PopupCharts = ({ chartData }) => {
  // const [dataToday, setDataToday] = React.useState(data.today.value);
  console.log(chartData);
  const numericData = chartData.map((data) => data.toNumber());
  console.log(numericData);

  const options = {
    chart: {
      type: 'line',
    },
    title: {
      text: null,
    },
    legend: {
      enabled: false,
    },
    accessibility: {
      enabled: false,
      point: {
        valueDescriptionFormat: '{xDescription}{separator}{value} million(s)',
      },
    },
    series: [
      {
        keys: ['y', 'color'],
        // data: [
        //   [4805121, '#eb0014'],
        //   [480521, '#fb0004'],
        //   [dataToday, '#ff0000'],
        // ],
        data: [chartData.today],
        color: {
          linearGradient: {
            x1: 0,
            x2: 0,
            y1: 1,
            y2: 0,
          },
          stops: [
            [0, '#0000ff'],
            [1, '#ff0000'],
          ],
        },
      },
    ],
  };

  return <HighchartsReact highcharts={Highcharts} options={options} />;
};
