import React from 'react';
import styles from './TableRow.module.scss';

import { PopupCharts } from '../PopupCharts';

export const TableRow = ({ data }) => {
  const [selectedData, setSelectedData] = React.useState(null);

  const openChart = (data) => {
    setSelectedData(data);
  };

  return (
    <>
      <tr onClick={() => openChart()}>
        <td className={styles.td}>{data.indicator}</td>
        <td className={styles.td}>{data.today}</td>
        <td className={styles.td}>{data.yesterday}</td>
        <td className={styles.td}>{data.thisday}</td>
      </tr>
      {selectedData && (
        <tr>
          <div>
            <PopupCharts />
          </div>
        </tr>
      )}
    </>
  );
};
