import React from 'react';
import styles from './TableRow.module.scss';

import { PopupCharts } from '../PopupCharts';

export const TableRow = ({ data }) => {
  const [selectedData, setSelectedData] = React.useState(null);

  const openChart = (rowData) => {
    setSelectedData([rowData.today, rowData.yesterday, rowData.thisday]);
  };

  const toggleChart = (rowData) => {
    // Если данные уже открыты, то закрываем их, иначе открываем
    setSelectedData((prevSelectedData) =>
      prevSelectedData ? null : [rowData.today, rowData.yesterday, rowData.thisday],
    );
  };

  return (
    <>
      <tr onClick={() => openChart(data)}>
        <td className={styles.td}>{data.indicator}</td>
        <td className={styles.td}>{data.today}</td>
        <td className={styles.td}>{data.yesterday}</td>
        <td className={styles.td}>{data.thisday}</td>
      </tr>
      {selectedData && (
        <tr>
          <td colSpan={4}>
            <PopupCharts chartData={selectedData} />
          </td>
        </tr>
      )}
    </>
  );
};
