import React from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

export const PopupCharts = () => {
  const options = {
    chart: {
      type: 'line',
    },
    accessibility: {
      enabled: false,
      point: {
        valueDescriptionFormat: '{xDescription}{separator}{value} million(s)',
      },
    },
    series: [
      {
        name: '',
        keys: ['y', 'color'],
        data: [
          [16, '#0000ff'],
          [361, '#8d0073'],
          [1018, '#ba0046'],
          [2025, '#d60028'],
          [3192, '#eb0014'],
          [4673, '#fb0004'],
          [5200, '#ff0000'],
        ],
        color: {
          linearGradient: {
            x1: 0,
            x2: 0,
            y1: 1,
            y2: 0,
          },
          stops: [
            [0, '#0000ff'],
            [1, '#ff0000'],
          ],
        },
      },
    ],
  };

  return;
  <>
    <HighchartsReact highcharts={Highcharts} options={options} />;
  </>;
};
